import {
  Streamlit,
  StreamlitComponentBase,
  withStreamlitConnection,
} from "streamlit-component-lib"
import React, { ReactNode } from "react"

import Reveal from 'reveal.js';
import Markdown from 'reveal.js/plugin/markdown/markdown';

interface State {
  content: string
  allowHTML: boolean
  config: object
}

/**
 * This is a React-based component template. The `render()` function is called
 * automatically when your component should be re-rendered.
 */
class RevealSlides extends StreamlitComponentBase<State> {
  public state = { content: this.props.args["content"], allowHTML: this.props.args["unsafe_allow_html"], config: {} }

  public render = (): ReactNode => {

    return (
      <div className="reveal">
        <div className="slides">
          {this.state.allowHTML? this.state.content:
            <section data-markdown>
              <textarea data-template>
                {this.state.content}
              </textarea>
            </section>}
        </div>
      </div>
    )
  }

  public componentDidMount = (): void => {}

  public componentDidUpdate = (): void => {}
}

// "withStreamlitConnection" is a wrapper function. It bootstraps the
// connection between your component and the Streamlit app, and handles
// passing arguments from Python -> Component.
//
// You don't need to edit withStreamlitConnection (but you're welcome to!).
export default withStreamlitConnection(RevealSlides)
